public  findLast_seq_no() {
   
    FormRowSet formRowSet = new FormRowSet();
    formRowSet.setMultiRow(true);
    Class.forName("com.mysql.jdbc.Driver").newInstance();
    Connection  con = DriverManager.getConnection("jdbc:mysql://localhost:3307/jwdb?characterEncoding=UTF-8", "root", "");
 
    if(!con.isClosed()){
        String sql = "SELECT MAX(c_tower_seq_no) FROM app_fd_ofc_tower_tab"; 
        PreparedStatement stmt = con.prepareStatement(sql);
        ResultSet resultSet = stmt.executeQuery();
        FormRow formRow = new FormRow();
	 
        while (resultSet.next()) {    
        formRow.put("lastSeqNo", "NO is "+resultSet.getString(1));
        formRowSet.add(formRow);
        }       
    } else {
        System.out.println("Connection Problem");
    }
    con.close();
   ////// return formRowSet;
}
return findLast_seq_no();
